# @title Defining helper funcitons
from datetime import datetime
import pytz

def getCurrentLocalTime():
    return datetime.now(pytz.timezone("Etc/GMT-5")).strftime("%H:%M:%S")

def weighted_choice(items):
    weights = [item['size'] for item in items]
    return random.choices(items, weights=weights)[0]

def random_height():
    feet = random.choices([4, 5, 6], weights=[1, 5, 1])[0]  # Weighted choice for feet
    inches = random.randint(0, 11)  # Possible inches in a foot
    return f"{feet}'{inches}"

def get_occupation(age):
    if (age < 25 and random.random() < 0.7): return "Student"
    if (random.random() < 0.05): return "Unemployed"
    if (age > 65 and random.random() < 0.7): return "Retired"
    return random.choice(occupations)



